int anf(int *TT)	//Finds algebraic degree of TT
{
	int i,j,k,it,p,su,Maxi=0;
	for (i=0;i<Zm;i++)
		T[i]=TT[i];

	for (i=0;i<Zn;i++)
	{
		k=0;
		su=0;
		for (j=0;j<(1<<i);j++)
		{
			p=(1<<(Zn-i));
			for (it=k;it<k+p;it++)		t[it-k]=T[it];
			k=k+p;
			for (it=p/2;it<p;it++)		t[it]=t[it-p/2]^t[it];
			for (it=su;it<su+p;it++)	u[it]=t[it-su];
			su=su+p;
		}
		for (j=0;j<Zm;j++)
			T[j]=u[j];
	}

	for (i=0;i<Zm;i++)
		if (T[i]==1 && BS[i]>Maxi)
			Maxi=BS[i];

	return Maxi;
}

void fastwh(int *FW, int *TTs)	//Computes Walsh spectrum values in integer format
{	
	int i,j,i1,i2,i3,k1=2,k2=Zm/2,k3=1,L1,temp1,temp2;
	for (i=0;i<Zm;i++)
		FW[i]=TTs[i];
	for (i1=0;i1<Zn;i1++)  
	{
	   L1=1;
	   for (i2=0;i2<k2;i2++)
	   {
		  for (i3=0;i3<k3;i3++)
		  {
			 i=i3+L1-1; j=i+k3; 
		     temp1= FW[i]; temp2 = FW[j]; 
			 FW[i]=temp1+temp2;
		     FW[j]=temp1-temp2;
		  }
	      L1=L1+k1; 
	   }
	   k1=k1*2; k2=k2/2; k3=k3*2;
	}
}

void fastwhld(long double *FW, long double *TTs)	//Computes Walsh spectrum values in long double format
{	
	int i,j,i1,i2,i3,k1=2,k2=Zm/2,k3=1,L1;
	long double temp1,temp2;
	for (i=0;i<Zm;i++)
		FW[i]=TTs[i];
	for (i1=0;i1<Zn;i1++)  
	{
	   L1=1;
	   for (i2=0;i2<k2;i2++)
	   {
		  for (i3=0;i3<k3;i3++)
		  {
			 i=i3+L1-1; j=i+k3; 
		     temp1= FW[i]; temp2 = FW[j]; 
			 FW[i]=temp1+temp2;
		     FW[j]=temp1-temp2;
		  }
	      L1=L1+k1; 
	   }
	   k1=k1*2; k2=k2/2; k3=k3*2;
	}
}


int acor(int *FW)	//Computes absolute indicator 
{
	int i;
	long double *AC=(long double *)malloc(Zm*sizeof(long double));
	long double *ACt=(long double *)malloc(Zm*sizeof(long double));
	for (i=0;i<Zm;i++)
		ACt[i]=((long double) FW[i])*((long double) FW[i]);
	fastwhld(AC,ACt);

	long double D,Maxi=-1;
	for (i=1;i<Zm;i++)
	{
		D=AC[i];
		if (AC[i]<0)
			D=-AC[i];
		if (D>Maxi)
			Maxi=D;
	}
	free(AC);
	free(ACt);
	return ((int) (Maxi/Zm));
}

int findmax(int *tt)	// Finds maximum absolute value
{
	int i;
	int D,Maxi=-1;
	for (i=0;i<Zm;i++)
	{
		D=tt[i];
		if (tt[i]<0)
			D=-tt[i];
		if (D>Maxi)
			Maxi=D;
	}
	return Maxi;
}

int conv_bal(int d, int NL, int acx, int zw)	// Makes TT balanced
{
	int i,j=0,dx,zwx,NLx,acxx;
	for (i=0;i<Zm;i++)
		if (FW[i]==0)
		{
			wb=i;
			if (j==0)
				break;
			j=j+1;
		}
	for (i=0;i<Zm;i++)
		TT[i]=((1-TT[i])/2)^(BS[(wb&i)]%2);
	dx=anf(TT);
	for (i=0;i<Zm;i++)
		TT[i]=1-2*TT[i];
	fastwh(FW,TT);
	zwx=0;
	for (i=0;i<Zm;i++)
		if (FW[i]==0)
			zwx=zwx+1;
	NLx=Zm/2-findmax(FW)/2;
	acxx=acor(FW);
	if (NLx!=NL || acxx!=acx || zwx!=zw || dx!=d || FW[0]!=0)
	{
		printf("\nError NL=%d acx=%d zw=%d d=%d ; NLx=%d acxx=%d zwx=%d dx=%d",NL,acx,zw,d,NLx,acxx,zwx,dx);
		return 1;
	}
	return 0;
}


int rnk_cnt()	//Finds rank of the zero set given by ZRS
{
	int i,j,k,s,ct;

	ct=0;
	for (i=0;i<kz;i++)
	{
		if (ZRS[i]!=-1 && ZRS[i]!=0)
		{
			A[ct]=ZRS[i];
			ZRS[i]=-1;
			ct=ct+1;
			if (ct!=15)
				for (j=1;j<(1<<ct);j++)
				{
					s=0;
					for (k=0;k<ct;k++)
						if (((j&(1<<(ct-k-1)))>>(ct-k-1))!=0)
							s=s^A[k];
					for (k=0;k<kz;k++)
						if (s==ZRS[k])
						{
							ZRS[k]=-1;
							break;
						}
				}
			else
				break;
		}
	}
	return ct;
}

int chk_res() //Checks if the set of zeros in the Walsh spectrum is of full rank
{
	int I,i,ZRSt[gn],rk;
	kz=0;

	for (i=0;i<Zm;i++)
		if (FW[i]==0)
		{
			ZRS[kz]=i;
			kz=kz+1;
		}

	for (i=0;i<kz;i++)
		ZRSt[i]=ZRS[i];
	for (I=0;I<kz;I++)
	{
		for (i=0;i<kz;i++)
			ZRS[i]=ZRS[i]^ZRSt[I];
		rk=rnk_cnt();
		if (rk==15)
		{
			for (i=0;i<kz;i++)
				ZRS[i]=ZRSt[i];
			return ZRSt[I];
		}
	}
	for (i=0;i<kz;i++)
		ZRS[i]=ZRSt[i];
	return -1;
}

int make_res(int d, int NL, int acx, int zw, int wr) //Makes TT resilient
{
	int i,j,k,B[Zn][Zn],vt[Zn],dx,NLx,acxx,zwx,ro;
	int r1[]={16384,8192,4096,2048,1024,512,256,128,64,32,16,8,4,2,1};
	for (i=0;i<Zn;i++)
		for (j=0;j<Zn;j++)
			B[i][j]=DB[A[i]*Zn+j];
	for (i=0;i<Zm;i++)
		TT[i]=((1-TT[i])/2)^(BS[(wr&i)]%2);
	for (i=0;i<Zm;i++)
	{
		for (j=0;j<Zn;j++)
			vt[j]=(BS[i&A[j]]%2);
		k=0;
		for (j=0;j<Zn;j++)
			k=k^(vt[j]<<(Zn-j-1));
		Tres[k]=TT[i];

	}
	dx=anf(Tres);
	for (i=0;i<Zm;i++)
		TT[i]=1-2*Tres[i];
	fastwh(FW,TT);
	zwx=0;
	for (i=0;i<Zm;i++)
		if (FW[i]==0)
			zwx=zwx+1;
	NLx=Zm/2-findmax(FW)/2;
	acxx=acor(FW);
	ro=1;
	if (FW[0]!=0)
		ro=0;
	for (i=0;i<Zn;i++)
		if (FW[r1[i]]!=0)
			ro=0;
	if (NLx!=NL || acxx!=acx || zwx!=zw || dx!=d || ro!=1)
	{
		printf("\nError NL=%d acx=%d zw=%d d=%d ro=%d; NLx=%d acxx=%d zwx=%d dx=%d",NL,acx,zw,d,ro,NLx,acxx,zwx,dx,ro);
		return 1;
	}
	return 0;
}